import Avatar from "@material-ui/core/Avatar";
import Button from "@material-ui/core/Button";
import { useTheme } from "@material-ui/core/styles";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import { ROUTES } from "../../constants";
import React, { useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import SettingMenu from "./SettingMenu";
import useStyle from "./style";
import { cloudinaryImgOptimize } from 'helper';

function Navigation() {
  const classes = useStyle();
  const theme = useTheme();
  const isXsDevice = useMediaQuery(theme.breakpoints.up("xs"));

  const { isAuth, user } = useSelector((state) => state.authReducer);
  const avtSrc = cloudinaryImgOptimize(user.avatar, 48, 48);
  const [showInput, setShowInput] = useState(isXsDevice);
  const [anchorMenu, setAnchorMenu] = useState(null);

  const onOpenMenu = (e) => setAnchorMenu(e.currentTarget);
  const onCloseMenu = () => setAnchorMenu(null);

  return (
    <div className={`${classes.navWrapper} w-100vw`} id="dynoNav">
      <div className={`${classes.nav} w-100`}>
        <div className="container h-100 flex-center--ver">
          {(isXsDevice || !showInput) && (
            <><Link to={ROUTES.HOME}>
              <img
                className={`${classes.imgSize} ${classes.logo}`}
                src={"https://res.cloudinary.com/phongvn2611/image/upload/v1637319049/english/avatar/logo-horizon_vmirgt.png"}
                alt="Logo" />
            </Link>

            { user && user.roleType=="admin" && (
           <> <div className="container" align="center">
            <div className={`content-no-sidebar ${classes.bgp}`} >
            <div class="content-no-sidebar b-g-p">
              <a href="#navIndex-0" data-nav-index="navIndex-0" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Courses</a>
              <a href="#navIndex-1" data-nav-index="navIndex-1" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Features</a>
              <a href="#navIndex-2" data-nav-index="navIndex-2" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Grammar</a>
              <a href="#navIndex-3" data-nav-index="navIndex-3" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Vocabulary</a>
              <a href="#navIndex-4" data-nav-index="navIndex-4" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Pronunciation</a>
              <a href="#navIndex-5" data-nav-index="navIndex-5" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">News</a>
              <a href="#navIndex-6" data-nav-index="navIndex-6" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Business</a>
              <a href="#navIndex-7" data-nav-index="navIndex-7" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">For Teachers</a>
              <a href="#navIndex-8" data-nav-index="navIndex-8" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">For Children</a>
              <a href="#navIndex-9" data-nav-index="navIndex-9" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Quizzes</a>
              <a href="#navIndex-10" data-nav-index="navIndex-10" class=" bbcle-expandable-nav-button bbcle-nav-button" data-nosynd="1">Our App</a>
              <div class="bbcle-nav-controls">
                <a data-nav-index="navIndex-search" class="bbcle-expandable-nav-button bbcle-nav-search bbcle-nav-button  open" data-nosynd="1" href="#navIndex-search"><span data-i18n-message-id="Search" class="not-translated _bbcle_translate_wrapper" lang="en">Search</span></a>
                </div></div>
           </div></div></>
            )}
            </>
          )}
          <div className={`${classes.control} flex-center--ver`}>
            {showInput && !isXsDevice && (
              <ArrowBackIosIcon
                className={`${classes.iconSize} mr-4 cur-pointer`}
                onClick={() => setShowInput(!showInput)}
              />
            )}

            {isAuth ? (
              <div className={classes.auth} onClick={onOpenMenu}>
                <Avatar
                  className={`${classes.imgSize} ${classes.avt} cur-pointer`}
                  alt="Username"
                  src={avtSrc} />
                <p className={classes.name}>
                  {user.name}
                </p>
              </div>

            ) : (
              <div>
                <Link to={ROUTES.REGISTER}>
                  <Button
                    className="_btn _btn-primary"
                    classes={{
                      root: classes.registerBtn,
                      label: classes.registerLabel,
                    }}
                    variant="contained"
                    color="primary"
                    size="small"
                  >
                    Đăng ký
                  </Button>
                </Link>
                <Link to={ROUTES.LOGIN}>
                  <Button
                    className="_btn _btn-primary"
                    classes={{
                      root: classes.loginBtn,
                      label: classes.loginLabel,
                    }}
                    variant="contained"
                    color="primary"
                    size="small"
                  >
                    Đăng nhập
                  </Button>
                </Link>
              </div>
            )}
            <SettingMenu anchorEl={anchorMenu} onClose={onCloseMenu} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Navigation;
