import React, { useRef } from "react";
import JoditEditor from "jodit-react";
JoditEditor.defaultOptions.controls.selectAll = {
	tooltip: 'Select all content',
	command: 'selectall'
};
const config = JoditEditor.make('#editor', {
	buttons: [...JoditEditor. defaultOptions.buttons, 'selectAll']
});
const RichTextEditor = ({ initialValue, getValue }) => {
  const editor = useRef(null);

  return (
    <JoditEditor
      ref={editor}
      value={initialValue}
      config={config}
      tabIndex={1}
      //   onBlur={(newContent) => getValue(newContent)}
      onChange={(newContent) => getValue(newContent)}
    />
  );
};

export default RichTextEditor;
